import React from 'react';
import Footer from '../Componentes/footer';

export default function EditComponentPage() {
    const cardStyle = {
        width: '131px',
        height: '198px',
        margin: '10px',
        textAlign: 'center',
        boxShadow: '5px 0 10px rgba(0, 0, 0, 0.1)',
        border: 'none'
    };

    const containerStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: 'calc(100vh - 100px)',  // Ajustando a altura mínima para ocupar toda a viewport menos a altura do footer
        paddingTop: '50px',  // Espaçamento superior ajustado
        marginBottom: '50px',  // Espaçamento inferior ajustado
        overflow: 'hidden'  // Escondendo overflow para evitar barra de rolagem
    };

    const rowStyle = {
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        maxWidth: '900px',  // Limitando a largura máxima para evitar que os cartões se espalhem demais
        margin: '0 auto'  // Centralizando os elementos na página
    };

    return (
        <div style={containerStyle}>
            <div style={rowStyle}>
                {[...Array(6)].map((_, index) => (
                    <div key={index} className="card" style={cardStyle}>
                        <img src="" style={{ width: '100%', height: '100%', objectFit: 'cover' }} alt="..." />
                        <div className="card-body" style={{ padding: '10px' }}>
                            <h5 className="card-title mb-2">Nome Software</h5>
                            <p className="licenças mb-2">licenças</p>
                        </div>
                    </div>
                ))}
            </div>

            <div style={{ ...rowStyle, marginBottom: '0' }}>
                {[...Array(4)].map((_, index) => (
                    <div key={index} className="card" style={cardStyle}>
                        <img src="" style={{ width: '100%', height: '100%', objectFit: 'cover' }} alt="..." />
                        <div className="card-body" style={{ padding: '10px' }}>
                            <h5 className="card-title mb-2">Nome Software</h5>
                            <p className="licenças mb-2">licenças</p>
                        </div>
                    </div>
                ))}
            </div>

            <Footer />
        </div>
    );
}
