'use strict';

var define = require('define-properties');
var callBind = require('call-bind');

var implementation = require('arraybuffer.prototype.slice/implementation');
var getPolyfill = require('arraybuffer.prototype.slice/polyfill');
var shim = require('arraybuffer.prototype.slice/shim');

var bound = callBind(getPolyfill());

define(bound, {
	getPolyfill: getPolyfill,
	implementation: implementation,
	shim: shim
});

module.exports = bound;
